import './App.css';
import { useState } from 'react';

function App() {
  const [variables, setVars] = useState("");
  const [values, setVals] = useState("");
  const [func, setFunc] = useState("");

  const updateVars = value => {
    setVars(variables + ", " + value);
  }

  const updateVals = value => {
    setVars(values + ", " + value);
  }

  const updateFunc = value => {
    setVars(func + value);
  }
  /**
   * This function creates the digits that can be used in the app.
   * @returns a list of number buttons to be displayed.
   */
  const importNumbers = () => {
    const numbers = [];
    for (let i = 0; i< 10; i++){
      numbers.push(<button  onClick={()=>updateFunc(i.toString())} key={i}>{i}</button>);
    }
    return numbers;
  }

  /**
   * This function creates the variables that can be used in the app.
   * @returns a list of variable buttons to be displayed.
   */
  const importVariables = () => {
    const variables = [];
    for (let i = 97; i< 123; i++){
      variables.push(<button onClick={()=>updateFunc(i.toString())} key={i}>{String.fromCharCode(i)}</button>);
    }
    for (let i = 65; i< 91; i++){
      variables.push(<button onClick={()=>updateFunc(i.toString())} key={i}>{String.fromCharCode(i)}</button>);
    }
    return variables;
  }

  return (
    <div className="App">
      <div className="AutoDiff">
        <div className='functionInput'>

        </div>
        <div className="Operators">
          <button onClick={()=>updateFunc('Log')}>Log</button>
          <button onClick={()=>updateFunc('Sin')}>Sin</button>
          <button onClick={()=>updateFunc('Cos')}>Cos</button>
          <button onClick={()=>updateFunc('+')}>+</button>
          <button onClick={()=>updateFunc('-')}>-</button>
          <button onClick={()=>updateFunc('/')}>/</button>
          <button onClick={()=>updateFunc('*')}>*</button>
          <button onClick={()=>updateFunc('^')}>^</button>
          <button onClick={() => updateFunc('(')}>(</button>
          <button onClick={() => updateFunc(')')}>)</button>
        </div>
        <div className="Numbers">
          {importNumbers()}
        </div>
        <div className="Variables">
          {importVariables()}
        </div>
        <div className="calculate">
          <button>Calculate</button>
        </div>
      </div>
    </div>
  );
}

export default App;
